sql.where:
1 = 1

    AND ACCOUNTID= #{accountId}



    AND exec_day= #{day}


    AND exec_month= #{month}



    AND exec_year= #{year}



    AND id= #{movementId}



    AND EXECUTEDBY= #{user}



    AND CATEGORYID= #{categoryId}

mapper.select:
select
  CATEGORYID as categoryId,
  TYPE as type,
  SUM(AMOUNT) as total
FROM MOVEMENTS

    1 = 1

        AND ACCOUNTID= #{accountId}



        AND exec_day= #{day}


        AND exec_month= #{month}



        AND exec_year= #{year}



GROUP BY CATEGORYID, TYPE
ORDER BY TYPE

mapper.update:
UPDATE  PUBLIC.AUTO_MOVEMENT_SETTINGS
   SET  multiplier = multiplier+1,
        last_exec = #{inDate}
WHERE   id=  #{id}
